'use client'

import { useState } from 'react'
import { motion } from 'framer-motion'
import { sendPasswordResetEmail } from '@/app/actions/auth'
import Link from 'next/link'
import { Input } from '@/ui/primitives/Input'
import { Loader2, ArrowLeft } from 'lucide-react'

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState('')
  const [isSubmitted, setIsSubmitted] = useState(false)
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!email) return

    setStatus('loading')
    setErrorMessage('')

    try {
      const result = await sendPasswordResetEmail(email)
      if (result.success) {
        setIsSubmitted(true)
        setStatus('success')
      } else {
        setStatus('error')
        setErrorMessage(result.message || '送信に失敗しました')
      }
    } catch (e) {
      setStatus('error')
      setErrorMessage('予期せぬエラーが発生しました')
    }
  }

  if (isSubmitted) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-md mx-auto space-y-8 text-center"
      >
        <h2 className="text-2xl font-bold text-gray-900">メールを確認してください</h2>
        <p className="mt-4 text-gray-600">
          <strong>{email}</strong> 宛にパスワード再設定用のメールを送信しました。<br />
          メール内のリンクをクリックして、新しいパスワードを設定してください。
        </p>
        <div className="mt-8">
          <Link
            href="/login"
            className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
          >
            ログイン画面に戻る
          </Link>
        </div>
      </motion.div>
    )
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="w-full max-w-md mx-auto space-y-8"
    >
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900">パスワード再設定</h2>
        <p className="mt-2 text-sm text-gray-600">
          登録済みのメールアドレスを入力してください。<br />
          再設定用のリンクを送信します。
        </p>
      </div>

      <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            メールアドレス
          </label>
          <div className="mt-1">
            <Input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              placeholder="example@email.com"
            />
          </div>
        </div>

        {status === 'error' && (
          <div className="text-red-600 text-sm text-center">
            {errorMessage}
          </div>
        )}

        <div>
          <button
            type="submit"
            disabled={status === 'loading'}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {status === 'loading' ? (
              <Loader2 className="animate-spin h-5 w-5" />
            ) : (
              '送信する'
            )}
          </button>
        </div>

        <div className="text-center">
          <Link
            href="/login"
            className="inline-flex items-center text-sm font-medium text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            ログイン画面に戻る
          </Link>
        </div>
      </form>
    </motion.div>
  )
}

'use client'

import { useState, useEffect } from 'react'
import { RegistrationData } from '../onboarding/RegistrationWizard'
import { completeRegistration } from '@/app/actions/auth'
import Link from 'next/link'
import { CheckCircle, AlertCircle, Loader2 } from 'lucide-react'

type Props = {
  data: RegistrationData
}

export default function RegisterCompleteStep({ data }: Props) {
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')

  useEffect(() => {
    const submit = async () => {
      setStatus('loading')
      try {
        const result = await completeRegistration(data)
        if (result.success) {
          setStatus('success')
        } else {
          setStatus('error')
          setErrorMessage(result.message || '登録処理に失敗しました')
        }
      } catch (e: any) {
        setStatus('error')
        setErrorMessage(e.message || '予期せぬエラーが発生しました')
      }
    }

    if (status === 'idle') {
      submit()
    }
  }, [data, status])

  if (status === 'loading') {
    return (
      <div className="flex flex-col items-center justify-center py-12">
        <Loader2 className="h-12 w-12 text-indigo-600 animate-spin mb-4" />
        <h3 className="text-lg font-medium text-gray-900">登録処理中...</h3>
        <p className="text-gray-500">アカウントとプロフィールを作成しています。</p>
      </div>
    )
  }

  if (status === 'error') {
    return (
      <div className="text-center py-8">
        <div className="mx-auto flex items-center justify-center h-12 w-12 rounded-full bg-red-100 mb-4">
          <AlertCircle className="h-6 w-6 text-red-600" />
        </div>
        <h3 className="text-lg font-medium text-gray-900 mb-2">登録に失敗しました</h3>
        <p className="text-gray-500 mb-6">{errorMessage}</p>
        <button
          onClick={() => window.location.reload()}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          やり直す
        </button>
      </div>
    )
  }

  return (
    <div className="text-center py-8">
      <div className="mx-auto flex items-center justify-center h-16 w-16 rounded-full bg-green-100 mb-6">
        <CheckCircle className="h-10 w-10 text-green-600" />
      </div>
      <h3 className="text-2xl font-bold text-gray-900 mb-2">登録完了！</h3>
      <p className="text-gray-600 mb-8">
        これですべての手続きが完了しました。<br />
        ダッシュボードへお進みください。
      </p>

      <div className="space-y-4">
        <Link
          href="/"
          className="inline-flex w-full justify-center py-3 px-4 border border-transparent text-base font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 shadow-sm"
        >
          ダッシュボードへ
        </Link>
      </div>
    </div>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { AnimatePresence, motion } from 'framer-motion'
import { createClient } from '@/lib/supabase/client'
import SetPasswordStep from './_components/SetPasswordStep'
import RegisterParentStep from '../_components/RegisterParentStep'
import RegisterChildrenStep from '../_components/RegisterChildrenStep'
import RegisterConfirmStep from '../_components/RegisterConfirmStep'
import RegisterCompleteStep from '../_components/RegisterCompleteStep'

export type RegistrationData = {
  account: {
    email: string
    password: string
  }
  parent: {
    lastName: string
    firstName: string
    lastNameKana: string
    firstNameKana: string
    phone: string
    address: string
  }
  children: Array<{
    lastName: string
    firstName: string
    lastNameKana: string
    firstNameKana: string
    birthday: string
    gender: string
    allergies: string
    notes: string
  }>
}

const initialData: RegistrationData = {
  account: { email: '', password: '' },
  parent: { lastName: '', firstName: '', lastNameKana: '', firstNameKana: '', phone: '', address: '' },
  children: []
}

type Props = {
  admissionFee: number
  annualFee: number
}

export default function RegistrationWizard({ admissionFee, annualFee }: Props) {
  const [step, setStep] = useState(0) // 0 indicates initializing
  const [formData, setFormData] = useState<RegistrationData>(initialData)
  const [isOAuthUser, setIsOAuthUser] = useState(false)
  const totalSteps = 5

  useEffect(() => {
    const init = async () => {
      const supabase = createClient()
      const { data: { user } } = await supabase.auth.getUser()

      if (user) {
        const email = user.email || ''
        const providers = user.app_metadata.providers || []
        const isOAuth = providers.length > 0 && !providers.includes('email') || providers.includes('google')

        // Extract names from OAuth metadata
        const metadata = user.user_metadata || {}
        let lastName = metadata.family_name || ''
        let firstName = metadata.given_name || ''

        // If Google doesn't provide family/given separately, try parsing full_name
        if (!lastName && !firstName && metadata.full_name) {
          const parts = metadata.full_name.split(' ')
          if (parts.length >= 2) {
            lastName = parts[0]
            firstName = parts.slice(1).join(' ')
          } else {
            lastName = metadata.full_name
          }
        }

        console.log('[RegistrationWizard] User Info:', { email, providers, isOAuth, metadata })

        setFormData(prev => ({
          ...prev,
          account: { ...prev.account, email },
          parent: {
            ...prev.parent,
            lastName: lastName || prev.parent.lastName,
            firstName: firstName || prev.parent.firstName,
          }
        }))
        setIsOAuthUser(isOAuth)

        // If OAuth user, start from step 2 (Parent info)
        // Otherwise start from step 1 (Password)
        setStep(isOAuth ? 2 : 1)
      } else {
        setStep(1)
      }
    }
    init()
  }, [])

  /* Resolved updateFormData */
  const updateFormData = <K extends keyof RegistrationData>(section: K, data: RegistrationData[K]) => {
    setFormData(prev => ({
      ...prev,
      [section]: Array.isArray(prev[section])
        ? data
        : { ...(prev[section] as object), ...(data as object) } as RegistrationData[K]
    }))
  }

  const updatePassword = (data: { password: string }) => {
    setFormData(prev => ({
      ...prev,
      account: { ...prev.account, password: data.password }
    }))
  }

  const nextStep = () => setStep(prev => prev + 1)
  const prevStep = () => {
    if (isOAuthUser && step === 2) return
    setStep(prev => prev - 1)
  }

  if (step === 0) {
    return (
      <div className="flex justify-center py-12">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
      </div>
    )
  }

  return (
    <div className="overflow-hidden">
      <div className="mb-8">
        <div className="h-2 bg-muted rounded-full border border-border">
          <div
            className="h-2 bg-primary rounded-full transition-all duration-300"
            style={{ width: `${(step / totalSteps) * 100}%` }}
          />
        </div>
        <div className="flex justify-between mt-2 text-xs text-muted-foreground">
          {['パスワード', '保護者', 'お子様', '確認', '完了'].map((label, index) => {
            const stepNum = index + 1
            const isActive = step >= stepNum
            const isCompleted = step > stepNum

            // Highlight step 1 as completed for OAuth users even if they are on step 2
            const showCompleted = isCompleted || (isOAuthUser && stepNum === 1)
            const showActive = isActive || (isOAuthUser && stepNum === 1)

            return (
              <span
                key={label}
                className={`${showActive ? 'text-primary font-semibold' : ''} ${showCompleted ? 'text-green-600' : ''} transition-colors`}
              >
                {label}
              </span>
            )
          })}
        </div>
      </div>

      <AnimatePresence mode="wait">
        <motion.div
          key={step}
          initial={{ x: 20, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          exit={{ x: -20, opacity: 0 }}
          transition={{ duration: 0.2 }}
        >
          {step === 1 && (
            <SetPasswordStep
              data={{ password: formData.account.password }}
              updateData={updatePassword}
              onNext={nextStep}
            />
          )}
          {step === 2 && (
            <RegisterParentStep
              data={formData.parent}
              updateData={(data) => updateFormData('parent', data)}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 3 && (
            <RegisterChildrenStep
              data={formData.children}
              parentLastName={formData.parent.lastName}
              parentLastNameKana={formData.parent.lastNameKana}
              updateData={(data) => setFormData(prev => ({ ...prev, children: data }))}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 4 && (
            <RegisterConfirmStep
              data={formData}
              admissionFee={admissionFee}
              annualFeePerChild={annualFee}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 5 && (
            <RegisterCompleteStep data={formData} />
          )}
        </motion.div>
      </AnimatePresence>
    </div>
  )
}

'use client'

import { useState } from 'react'
import { motion } from 'framer-motion'
import { createClient } from '@/lib/supabase/client'
import Link from 'next/link'
import { Input } from '@/ui/primitives/Input'
import { Loader2, ArrowLeft } from 'lucide-react'
import { useRouter } from 'next/navigation'

export default function UpdatePasswordPage() {
  const [password, setPassword] = useState('')
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')
  const router = useRouter()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!password) return

    setStatus('loading')
    setErrorMessage('')

    try {
      const supabase = createClient()
      const { error } = await supabase.auth.updateUser({ password })

      if (!error || error.message.includes('New password should be different')) {
        setStatus('success')
        setTimeout(() => {
          router.push('/')
        }, 3000)
      } else {
        setStatus('error')
        setErrorMessage(error.message || 'パスワードの更新に失敗しました')
      }
    } catch (e) {
      setStatus('error')
      setErrorMessage('予期せぬエラーが発生しました')
    }
  }

  if (status === 'success') {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-md mx-auto space-y-8 text-center"
      >
        <h2 className="text-2xl font-bold text-gray-900">パスワードを更新しました</h2>
        <p className="mt-4 text-gray-600">
          新しいパスワードの設定が完了しました。<br />
          トップページへ移動します...
        </p>
      </motion.div>
