          </div>
        )}

        <div>
          <button
            type="submit"
            disabled={status === 'loading'}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {status === 'loading' ? (
              <Loader2 className="animate-spin h-5 w-5" />
            ) : (
              'パスワードを変更する'
            )}
          </button>
        </div>

        <div className="text-center">
          <Link
            href="/login"
            className="inline-flex items-center text-sm font-medium text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            ログイン画面に戻る
          </Link>
        </div>
      </form>
    </motion.div>
  )
}

import { createClient } from '@/lib/supabase/server'
import { calculateGrade, getGradeOrder } from '@/lib/grade-utils'
import { getTargetFiscalYear } from '@/lib/fiscal-year'
import { EventDetailScreen } from '@/components/screens/admin/events/EventDetailScreen'

export default async function AdminEventDetailPage({
  params,
}: {
  params: Promise<{ id: string }>
}) {
  const { id } = await params
  const supabase = await createClient()
  const currentFiscalYear = await getTargetFiscalYear()

  const { data: event } = await supabase
    .from('events')
    .select('*')
    .eq('id', id)
    .single()

  if (!event) {
    return <div>イベントが見つかりません</div>
  }

  const { data: participantsRaw } = await supabase
    .from('event_participants')
    .select(`
        *,
        profile:profiles(full_name, email, phone),
        child:children(full_name, last_name, first_name, birthday)
    `)
    .eq('event_id', id)
    .order('created_at', { ascending: true })

  const participants = (participantsRaw || []).map((p: any) => {
    const child = p.child
    const grade = child && child.birthday ? calculateGrade(child.birthday, currentFiscalYear) : ''
    const gradeOrder = getGradeOrder(grade)
    const childName = child ? `${child.last_name || ''} ${child.first_name || ''}`.trim() || child.full_name : '不明な子供'

    return {
      ...p,
      childName,
      grade,
      gradeOrder,
      profile: p.profile
    }
  })

  participants.sort((a: any, b: any) => {
    if (a.gradeOrder !== b.gradeOrder) {
      return a.gradeOrder - b.gradeOrder
    }
    return a.childName.localeCompare(b.childName, 'ja')
  })

  const attendingParticipants = participants.filter((p: any) => p.status === 'attending')
  const attendingCount = attendingParticipants.length

  return (
    <EventDetailScreen
      id={id}
      event={event}
      participants={participants}
      attendingParticipants={attendingParticipants}
      attendingCount={attendingCount}
    />
  )
}

'use client'

import { useState } from 'react'

type Template = {
  id: string
  name: string
  subject: string
  body: string
}

export default function TemplateSelector({
  templates,
  sendAction,
}: {
  templates: Template[]
  sendAction: (formData: FormData) => void
}) {
  const [selectedTemplateId, setSelectedTemplateId] = useState('')
  const [subject, setSubject] = useState('')
  const [body, setBody] = useState('')

  const handleTemplateChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
    const templateId = e.target.value
    setSelectedTemplateId(templateId)

    if (templateId) {
      const template = templates.find((t) => t.id === templateId)
      if (template) {
        setSubject(template.subject)
        setBody(template.body)
      }
    }
  }

  return (
    <form action={sendAction} className="space-y-6 bg-white p-6 shadow sm:rounded-md">
      <div>
        <label htmlFor="template" className="block text-sm font-medium leading-6 text-gray-900">
          テンプレートから選択
        </label>
        <div className="mt-2">
          <select
            id="template"
            name="template_id"
            className="block w-full rounded-md border-0 py-1.5 p-3 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            value={selectedTemplateId}
            onChange={handleTemplateChange}
          >
            <option value="">テンプレートを選択しない</option>
            {templates.map((t) => (
              <option key={t.id} value={t.id}>
                {t.name}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div>
        <label htmlFor="subject" className="block text-sm font-medium leading-6 text-gray-900">
          件名
        </label>
        <div className="mt-2">
          <input
            type="text"
            name="subject"
            id="subject"
            required
            className="block w-full rounded-md border-0 py-1.5 p-3 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            value={subject}
            onChange={(e) => setSubject(e.target.value)}
          />
        </div>
      </div>

      <div>
        <label htmlFor="body" className="block text-sm font-medium leading-6 text-gray-900">
          本文
        </label>
        <div className="mt-2">
          <textarea
            id="body"
            name="body"
            rows={10}
            required
            className="block w-full rounded-md border-0 py-1.5 p-3 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
            value={body}
            onChange={(e) => setBody(e.target.value)}
          />
        </div>
      </div>

      <div className="flex justify-end gap-3">
        <button
          type="submit"
          className="rounded-md bg-indigo-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
        >
          配信する
        </button>
      </div>
    </form>
  )
}

import { createClient } from '@/lib/supabase/server'
import { notFound } from 'next/navigation'
import { getTargetFiscalYear } from '@/app/admin/actions/settings'
import { UserEditScreen } from '@/components/screens/admin/users/UserEditScreen'

type MemberView = 'child' | 'guardian' | 'officer'

export default async function AdminUserEditPage({
  params,
  searchParams,
}: {
  params: Promise<{ id: string }>
  searchParams: Promise<{ [key: string]: string | string[] | undefined }>
}) {
  const { id } = await params
  const sParams = await searchParams
  const supabase = await createClient()

  const requestedView = sParams?.view as string | undefined
  const backView: MemberView =
    requestedView === 'guardian' || requestedView === 'officer' ? requestedView : 'child'

  const { data: profile } = await supabase
    .from('profiles')
    .select('*')
    .eq('id', id)
    .single()

  if (!profile) {
    notFound()
  }

  const { data: children } = await supabase
    .from('children')
    .select('*')
    .eq('parent_id', id)
    .is('deleted_at', null)
    .order('birthday', { ascending: false })

  const targetFiscalYear = await getTargetFiscalYear()

  return (
    <UserEditScreen
      id={id}
      profile={profile}
      children={children || []}
      targetFiscalYear={targetFiscalYear}
      backView={backView}
    />
  )
}

import { createClient } from '@/lib/supabase/server'
import Link from 'next/link'
import { submitRsvp } from '../../actions/rsvp'

// Ensure we handle async params correctly for Next.js 15+ 
export default async function EventPage({
  params,
}: {
  params: Promise<{ id: string }>
}) {
  const { id } = await params
  const supabase = await createClient()

  // Get event details
  const { data: event } = await supabase
    .from('events')
    .select('*')
    .eq('id', id)
    .single()

  if (!event) {
    return <div>イベントが見つかりません</div>
  }

  // Get current user's children and their rsvp status
  const { data: { user } } = await supabase.auth.getUser()

  let children: any[] = []
  const participantMap: Record<string, any> = {}

  if (user) {
    // 1. Get children
    const { data: userChildren } = await supabase
      .from('children')
      .select('*')
      .eq('parent_id', user.id)
      .order('birthday', { ascending: false }) // Sort by age (youngest first usually bottom, but birthday descending means youngest first?) 
      // Usually older child first is better? birthday ascending -> older first
      .order('created_at', { ascending: true }) // fallback

    // Sort by birthday ascending (Oldest first)
    if (userChildren) {
      children = userChildren.sort((a, b) => (a.birthday > b.birthday ? 1 : -1))
    }

    // 2. Get existing participants records for these children
    if (children.length > 0) {
      const { data: participants } = await supabase
        .from('event_participants')
        .select('*')
        .eq('event_id', id)
        .eq('profile_id', user.id)
        .not('child_id', 'is', null)

      if (participants) {
        participants.forEach(p => {
          if (p.child_id) participantMap[p.child_id] = p
        })
      }
    }
  }

  return (
    <div className="min-h-screen bg-gray-50 p-4 sm:p-8">
      <div className="mx-auto max-w-3xl">
        <div className="mb-4">
          <Link href="/" className="text-indigo-600 hover:text-indigo-500">
            ← ダッシュボードへ戻る
          </Link>
        </div>

        <div className="overflow-hidden bg-white shadow sm:rounded-lg">
          <div className="px-4 py-5 sm:px-6">
            <h3 className="text-xl font-semibold leading-6 text-gray-900">{event.title}</h3>
            <p className="mt-1 max-w-2xl text-sm text-gray-500">{event.type === 'meeting' ? '定例会' : 'イベント'}</p>
          </div>
          <div className="border-t border-gray-200 px-4 py-5 sm:px-6">
            <dl className="grid grid-cols-1 gap-x-4 gap-y-8 sm:grid-cols-2">
              <div className="sm:col-span-1">
                <dt className="text-sm font-medium text-gray-500">日時</dt>
                <dd className="mt-1 text-sm text-gray-900">
                  {new Date(event.scheduled_date).toLocaleDateString()}
                  {event.start_time && ` ${event.start_time.slice(0, 5)}`}
                  {event.scheduled_end_date && ` 〜 ${new Date(event.scheduled_end_date).toLocaleDateString()}`}
                </dd>
              </div>
              <div className="sm:col-span-1">
                <dt className="text-sm font-medium text-gray-500">場所</dt>
                <dd className="mt-1 text-sm text-gray-900">{event.location || '未定'}</dd>
              </div>
              <div className="sm:col-span-2">
                <dt className="text-sm font-medium text-gray-500">詳細</dt>
                <dd className="mt-1 text-sm text-gray-900 whitespace-pre-wrap">{event.description}</dd>
              </div>
            </dl>
          </div>

          {event.rsvp_required && (
            <div className="border-t border-gray-200 px-4 py-5 sm:px-6 bg-indigo-50/50">
              <h4 className="text-lg font-bold text-indigo-900">出欠登録</h4>

              {children.length === 0 ? (
                <div className="mt-4 p-4 bg-yellow-50 rounded border border-yellow-200 text-yellow-800">
                  <p>お子様の情報が登録されていません。「マイページ」からお子様を登録してください。</p>
                  <Link href="/profile" className="text-indigo-600 underline mt-2 inline-block">マイページへ</Link>
                </div>
              ) : (
                <>
                  {event.rsvp_deadline && (
                    <p className="text-sm text-red-600 mb-4 mt-1">締切: {new Date(event.rsvp_deadline).toLocaleString()}</p>
                  )}

                  <form action={submitRsvp} className="mt-6 space-y-4">
                    <input type="hidden" name="event_id" value={event.id} />

                    <div className="bg-white rounded-lg border border-gray-200 divide-y divide-gray-100">
                      {children.map(child => {
                        const rsvp = participantMap[child.id]
                        const isAttending = rsvp?.status === 'attending'

                        return (
                          <div key={child.id} className="p-4 flex items-center justify-between hover:bg-gray-50 transition-colors">
                            <label htmlFor={`status_${child.id}`} className="flex items-center gap-4 cursor-pointer flex-1">
                              <input
                                type="checkbox"
                                id={`status_${child.id}`}
                                name={`status_${child.id}`}
                                value="attending"
                                defaultChecked={isAttending}
                                className="h-5 w-5 rounded border-gray-300 text-indigo-600 focus:ring-indigo-600 cursor-pointer"
                              />
                              <div className="flex flex-col">
                                <span className="font-bold text-gray-900 text-lg">{child.full_name} <span className="text-sm font-normal text-gray-500">さん</span></span>
                                <span className="text-sm text-gray-500">
                                  参加する場合はチェック
                                </span>
                              </div>
                            </label>
                          </div>
                        )
                      })}
                    </div>

                    <div className="pt-4 flex justify-end">
                      <button
                        type="submit"
                        className="w-full sm:w-auto inline-flex justify-center rounded-md bg-indigo-600 px-8 py-3 text-base font-semibold text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
                      >
                        保存する
                      </button>
                    </div>
                  </form>
                </>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  )
}
