'use client'

import { useState } from 'react'
import { motion } from 'framer-motion'
import { sendPasswordResetEmail } from '@/app/actions/auth'
import Link from 'next/link'
import { Input } from '@/ui/primitives/Input'
import { Loader2, ArrowLeft } from 'lucide-react'

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState('')
  const [isSubmitted, setIsSubmitted] = useState(false)
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!email) return

    setStatus('loading')
    setErrorMessage('')

    try {
      const result = await sendPasswordResetEmail(email)
      if (result.success) {
        setIsSubmitted(true)
        setStatus('success')
      } else {
        setStatus('error')
        setErrorMessage(result.message || '送信に失敗しました')
      }
    } catch (e) {
      setStatus('error')
      setErrorMessage('予期せぬエラーが発生しました')
    }
  }

  if (isSubmitted) {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-md mx-auto space-y-8 text-center"
      >
        <h2 className="text-2xl font-bold text-gray-900">メールを確認してください</h2>
        <p className="mt-4 text-gray-600">
          <strong>{email}</strong> 宛にパスワード再設定用のメールを送信しました。<br />
          メール内のリンクをクリックして、新しいパスワードを設定してください。
        </p>
        <div className="mt-8">
          <Link
            href="/login"
            className="text-sm font-medium text-indigo-600 hover:text-indigo-500"
          >
            ログイン画面に戻る
          </Link>
        </div>
      </motion.div>
    )
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="w-full max-w-md mx-auto space-y-8"
    >
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900">パスワード再設定</h2>
        <p className="mt-2 text-sm text-gray-600">
          登録済みのメールアドレスを入力してください。<br />
          再設定用のリンクを送信します。
        </p>
      </div>

      <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email" className="block text-sm font-medium text-gray-700">
            メールアドレス
          </label>
          <div className="mt-1">
            <Input
              id="email"
              name="email"
              type="email"
              autoComplete="email"
              required
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              placeholder="example@email.com"
            />
          </div>
        </div>

        {status === 'error' && (
          <div className="text-red-600 text-sm text-center">
            {errorMessage}
          </div>
        )}

        <div>
          <button
            type="submit"
            disabled={status === 'loading'}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {status === 'loading' ? (
              <Loader2 className="animate-spin h-5 w-5" />
            ) : (
              '送信する'
            )}
          </button>
        </div>

        <div className="text-center">
          <Link
            href="/login"
            className="inline-flex items-center text-sm font-medium text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            ログイン画面に戻る
          </Link>
        </div>
      </form>
    </motion.div>
  )
}

import { useState, useEffect } from 'react'
import { RegistrationData } from '../onboarding/RegistrationWizard'
import { completeRegistration } from '@/app/actions/auth'
import Link from 'next/link'
import { CheckCircle, AlertCircle, Loader2 } from 'lucide-react'
import { Box } from '@/ui/layout/Box'
import { Stack } from '@/ui/layout/Stack'
import { Text } from '@/ui/primitives/Text'
import { Heading } from '@/ui/primitives/Heading'
import { Button } from '@/ui/primitives/Button'

type Props = {
  data: RegistrationData
}

export default function RegisterCompleteStep({ data }: Props) {
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')

  useEffect(() => {
    const submit = async () => {
      setStatus('loading')
      try {
        const result = await completeRegistration(data)
        if (result.success) {
          setStatus('success')
        } else {
          setStatus('error')
          setErrorMessage(result.message || '登録処理に失敗しました')
        }
      } catch (e: any) {
        setStatus('error')
        setErrorMessage(e.message || '予期せぬエラーが発生しました')
      }
    }

    if (status === 'idle') {
      submit()
    }
  }, [data, status])

  if (status === 'loading') {
    return (
      <Stack className="items-center justify-center py-12 gap-4">
        <Loader2 className="h-12 w-12 text-primary animate-spin" />
        <Stack className="gap-1 text-center">
          <Heading size="h3" className="text-lg font-bold">登録処理中...</Heading>
          <Text className="text-sm text-muted-foreground">アカウントとプロフィールを作成しています。</Text>
        </Stack>
      </Stack>
    )
  }

  if (status === 'error') {
    return (
      <Stack className="items-center justify-center py-8 gap-6 text-center">
        <Box className="flex items-center justify-center h-14 w-14 rounded-full bg-destructive/10 text-destructive">
          <AlertCircle size={32} />
        </Box>
        <Stack className="gap-2">
          <Heading size="h3" className="text-xl font-bold">登録に失敗しました</Heading>
          <Text className="text-sm text-muted-foreground max-w-sm mx-auto">{errorMessage}</Text>
        </Stack>
        <Button
          onClick={() => window.location.reload()}
          className="px-8 h-11 font-bold shadow-md"
          activeScale={true}
        >
          やり直す
        </Button>
      </Stack>
    )
  }

  return (
    <Stack className="items-center justify-center py-8 gap-8 text-center">
      <Box className="flex items-center justify-center h-20 w-20 rounded-full bg-green-100 text-green-600 shadow-sm border border-green-200">
        <CheckCircle size={48} />
      </Box>
      <Stack className="gap-3">
        <Heading size="h2" className="text-3xl font-extrabold tracking-tight">登録完了！</Heading>
        <Text className="text-base text-muted-foreground leading-relaxed">
          これですべての手続きが完了しました。<br />
          ダッシュボードへお進みください。
        </Text>
      </Stack>

      <Box className="w-full max-w-xs pt-4">
        <Button
          asChild
          className="w-full h-12 text-lg font-bold shadow-lg"
          activeScale={true}
        >
          <Link href="/">ダッシュボードへ</Link>
        </Button>
      </Box>
    </Stack>
  )
}

'use client'

import { useEffect, useState } from 'react'
import { AnimatePresence, motion } from 'framer-motion'
import { createClient } from '@/lib/supabase/client'
import SetPasswordStep from './_components/SetPasswordStep'
import RegisterParentStep from '../_components/RegisterParentStep'
import RegisterChildrenStep from '../_components/RegisterChildrenStep'
import RegisterConfirmStep from '../_components/RegisterConfirmStep'
import RegisterCompleteStep from '../_components/RegisterCompleteStep'
import { Box } from '@/ui/layout/Box'
import { Stack, HStack } from '@/ui/layout/Stack'
import { Text } from '@/ui/primitives/Text'
import { Loader2 } from 'lucide-react'
import { cn } from '@/lib/utils'

export type RegistrationData = {
  account: {
    email: string
    password: string
  }
  parent: {
    lastName: string
    firstName: string
    lastNameKana: string
    firstNameKana: string
    phone: string
    address: string
  }
  children: Array<{
    lastName: string
    firstName: string
    lastNameKana: string
    firstNameKana: string
    birthday: string
    gender: string
    allergies: string
    notes: string
  }>
}

const initialData: RegistrationData = {
  account: { email: '', password: '' },
  parent: { lastName: '', firstName: '', lastNameKana: '', firstNameKana: '', phone: '', address: '' },
  children: []
}

type Props = {
  admissionFee: number
  annualFee: number
}

export default function RegistrationWizard({ admissionFee, annualFee }: Props) {
  const [step, setStep] = useState(0) // 0 indicates initializing
  const [formData, setFormData] = useState<RegistrationData>(initialData)
  const [isOAuthUser, setIsOAuthUser] = useState(false)
  const totalSteps = 5

  useEffect(() => {
    const init = async () => {
      const supabase = createClient()
      const { data: { user } } = await supabase.auth.getUser()

      if (user) {
        const email = user.email || ''
        const providers = user.app_metadata.providers || []
        const isOAuth = providers.length > 0 && !providers.includes('email') || providers.includes('google')

        const metadata = user.user_metadata || {}
        let lastName = metadata.family_name || ''
        let firstName = metadata.given_name || ''

        if (!lastName && !firstName && metadata.full_name) {
          const parts = metadata.full_name.split(' ')
          if (parts.length >= 2) {
            lastName = parts[0]
            firstName = parts.slice(1).join(' ')
          } else {
            lastName = metadata.full_name
          }
        }

        setFormData(prev => ({
          ...prev,
          account: { ...prev.account, email },
          parent: {
            ...prev.parent,
            lastName: lastName || prev.parent.lastName,
            firstName: firstName || prev.parent.firstName,
          }
        }))
        setIsOAuthUser(isOAuth)
        setStep(isOAuth ? 2 : 1)
      } else {
        setStep(1)
      }
    }
    init()
  }, [])

  const updateFormData = <K extends keyof RegistrationData>(section: K, data: RegistrationData[K]) => {
    setFormData(prev => ({
      ...prev,
      [section]: Array.isArray(prev[section])
        ? data
        : { ...(prev[section] as object), ...(data as object) } as RegistrationData[K]
    }))
  }

  const updatePassword = (data: { password: string }) => {
    setFormData(prev => ({
      ...prev,
      account: { ...prev.account, password: data.password }
    }))
  }

  const nextStep = () => setStep(prev => prev + 1)
  const prevStep = () => {
    if (isOAuthUser && step === 2) return
    setStep(prev => prev - 1)
  }

  if (step === 0) {
    return (
      <Box className="flex items-center justify-center py-20">
        <Loader2 className="h-10 w-10 text-primary animate-spin" />
      </Box>
    )
  }

  const stepLabels = ['パスワード', '保護者', 'お子様', '確認', '完了']

  return (
    <Box className="w-full">
      <header className="mb-10 px-2">
        <Box className="relative">
          <Box className="h-1.5 bg-muted rounded-full overflow-hidden border border-border/50">
            <Box
              className="h-full bg-primary rounded-full transition-all duration-700 ease-out shadow-[0_0_10px_rgba(var(--primary),0.3)]"
              style={{ width: `${(step / totalSteps) * 100}%` }}
            />
          </Box>
        </Box>

        <HStack className="justify-between mt-4 px-1">
          {stepLabels.map((label, index) => {
            const stepNum = index + 1
            const isActive = step === stepNum
            const isCompleted = step > stepNum
            const isOAuthComplete = isOAuthUser && stepNum === 1

            return (
              <Stack key={label} className="items-center gap-1.5 relative group">
                <Box className={cn(
                  "w-2.5 h-2.5 rounded-full border-2 transition-all duration-300",
                  (isCompleted || isOAuthComplete) ? "bg-green-500 border-green-500 scale-125" :
                    isActive ? "bg-primary border-primary scale-125 shadow-[0_0_8px_rgba(var(--primary),0.5)]" :
                      "bg-muted border-border"
                )} />
                <Text
                  weight={isActive ? "bold" : "medium"}
                  className={cn(
                    "text-[10px] tracking-tight transition-colors duration-300",
                    (isCompleted || isOAuthComplete) ? "text-green-600" :
                      isActive ? "text-primary" :
                        "text-muted-foreground/60"
                  )}
                >
                  {label}
                </Text>

                {index < stepLabels.length - 1 && (
                  <Box className="hidden md:block absolute top-[5px] left-[50%] w-[calc(100%-8px)] h-px bg-border -z-10" />
                )}
              </Stack>
            )
          })}
        </HStack>
      </header>

      <AnimatePresence mode="wait">
        <motion.div
          key={step}
          initial={{ y: 10, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          exit={{ y: -10, opacity: 0 }}
          transition={{ duration: 0.3, ease: "easeOut" }}
          className="min-h-[400px]"
        >
          {step === 1 && (
            <SetPasswordStep
              data={{ password: formData.account.password }}
              updateData={updatePassword}
              onNext={nextStep}
            />
          )}
          {step === 2 && (
            <RegisterParentStep
              data={formData.parent}
              updateData={(data) => updateFormData('parent', data)}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 3 && (
            <RegisterChildrenStep
              data={formData.children}
              parentLastName={formData.parent.lastName}
              parentLastNameKana={formData.parent.lastNameKana}
              updateData={(data) => setFormData(prev => ({ ...prev, children: data }))}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 4 && (
            <RegisterConfirmStep
              data={formData}
              admissionFee={admissionFee}
              annualFeePerChild={annualFee}
              onNext={nextStep}
              onPrev={prevStep}
            />
          )}
          {step === 5 && (
            <RegisterCompleteStep data={formData} />
          )}
        </motion.div>
      </AnimatePresence>
    </Box>
  )
}

'use client'

import { useState } from 'react'
import { motion } from 'framer-motion'
import { createClient } from '@/lib/supabase/client'
import Link from 'next/link'
import { Input } from '@/ui/primitives/Input'
import { Loader2, ArrowLeft } from 'lucide-react'
import { useRouter } from 'next/navigation'

export default function UpdatePasswordPage() {
  const [password, setPassword] = useState('')
  const [status, setStatus] = useState<'idle' | 'loading' | 'success' | 'error'>('idle')
  const [errorMessage, setErrorMessage] = useState('')
  const router = useRouter()

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault()
    if (!password) return

    setStatus('loading')
    setErrorMessage('')

    try {
      const supabase = createClient()
      const { error } = await supabase.auth.updateUser({ password })

      if (!error || error.message.includes('New password should be different')) {
        setStatus('success')
        setTimeout(() => {
          router.push('/')
        }, 3000)
      } else {
        setStatus('error')
        setErrorMessage(error.message || 'パスワードの更新に失敗しました')
      }
    } catch (e) {
      setStatus('error')
      setErrorMessage('予期せぬエラーが発生しました')
    }
  }

  if (status === 'success') {
    return (
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="w-full max-w-md mx-auto space-y-8 text-center"
      >
        <h2 className="text-2xl font-bold text-gray-900">パスワードを更新しました</h2>
        <p className="mt-4 text-gray-600">
          新しいパスワードの設定が完了しました。<br />
          トップページへ移動します...
        </p>
      </motion.div>
    )
  }

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="w-full max-w-md mx-auto space-y-8"
    >
      <div className="text-center">
        <h2 className="text-2xl font-bold text-gray-900">新しいパスワードの設定</h2>
        <p className="mt-2 text-sm text-gray-600">
          新しいパスワードを入力してください。
        </p>
      </div>

      <form className="mt-8 space-y-6" onSubmit={handleSubmit}>
        <div>
          <label htmlFor="password" className="block text-sm font-medium text-gray-700">
            新しいパスワード
          </label>
          <div className="mt-1">
            <Input
              id="password"
              name="password"
              type="password"
              required
              minLength={8}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="appearance-none block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm placeholder-gray-400 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
              placeholder="8文字以上"
            />
          </div>
        </div>

        {status === 'error' && (
          <div className="text-red-600 text-sm text-center">
            {errorMessage}
          </div>
        )}

        <div>
          <button
            type="submit"
            disabled={status === 'loading'}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 disabled:opacity-50"
          >
            {status === 'loading' ? (
              <Loader2 className="animate-spin h-5 w-5" />
            ) : (
              'パスワードを変更する'
            )}
          </button>
        </div>

        <div className="text-center">
          <Link
            href="/login"
            className="inline-flex items-center text-sm font-medium text-gray-600 hover:text-gray-900"
          >
            <ArrowLeft className="mr-2 h-4 w-4" />
            ログイン画面に戻る
          </Link>
        </div>
      </form>
    </motion.div>
  )
}
